INSERT INTO `companies` (`id`, `name`, `slug`, `descript`, `logo`, `contact`, `address`) VALUES
	(1, 'Công Ty Cổ Phần Rượu Bia Nước Giải Khát Aroma', 'cong-ty-co-phan-ruou-bia-nuoc-giai-khat-aroma', 'Công ty cổ phần Rượu bia Nước giải khát Aroma - đã sáng tạo lên thương hiệu Men’Vodka - sản phẩm đang được rất nhiều người tiêu dùng tin tưởng và chiếm thị phần lớn trên thị trường Vodka Việt Nam hiện nay. Bên cạnh đó, nhãn hàng rượu truyền thống Sân Đình, Làng tôi cũng là được biết đến là sản phẩm có chất lượng và hương vị truyền thống tuyệt vời.', 'img/companies/cong-ty-co-phan-ruou-bia-nuoc-giai-khat-aroma.jpeg', 'Đỗ Bá Trường_-_-_R&amp;D_-_-_0988041615', 'Số 376 Đường Nguyễn Thiện Thuật, Tổ dân phố Yên Tập, Phường Nhân Hòa'),
	(2, 'Công ty TNHH Foseca Việt Nam', 'cong-ty-tnhh-foseca-viet-nam', 'Là đối tác đồng hành với “bộ phận kinh doanh điện thoại di động tại Việt Nam” của Samsung Electronics, FOSECA hiện cung cấp 54,000 suất ăn mỗi ngày (một trong những quy mô phục vụ suất ăn lớn nhất thế giới) cho nhà máy Samsung tại Bắc Ninh, Việt Nam. Đây là ví dụ thành công tiêu biểu cho việc đồng hành phát triển tại thị trường nước ngoài.', 'img/companies/cong-ty-tnhh-foseca-viet-nam.png', 'Đỗ Bá Trường_-_-_R&amp;D_-_-_0988041615', 'Số 18 Đường 10 KCN Đô Thị Và Dịch Vụ Vsip Bắc Ninh, X. Đại Đồng, H. Tiên Du, Bắc Ninh'),
	(3, 'Công Ty TNHH T.M.A', 'cong-ty-tnhh-tma', 'Công Ty TNHH T.M.A chuyên cấp các loại bột sữa, bột cacao, dầu ăn,..', 'img/companies/cong-ty-tnhh-tma.jpeg', 'Đỗ Bá Trường_-_-_Technical Food_-_-_0988041615', 'Số 5, Đường Số 1, Khu Nhà ở Chánh Hưng, P. 5, Q. 8,Tp. Hồ Chí Minh (TPHCM)');
/*!40000 ALTER TABLE `companies` ENABLE KEYS */;

